Drupal.behaviors.utility_bar_login_forms = function (context) {
  // TODO: Move these CSS changes into a conditional iOS CSS file
  if(navigator.platform == 'iPad' || navigator.platform == 'iPhone' || navigator.platform == 'iPod') {
    $('#page').css('position','absolute').css('top','30px');
    $("#utility-bar").css("bottom", null).css("top", 0); //put the utility bar at the top 
    $("#utility-bar #sign-in .pull-out").css('bottom', null).css('top','30px').css('left','-107px'); //put the pull-out below the utility bar and kick it a bit to the left
    $("#block-utility_bar-utility_bar_login_form h2.block-title").css('display','none');
    $("#utility-bar-wrapper").css('border-top-left-radius', '0px').css('border-top-right-radius', '0px').css('border-bottom-right-radius','5px').css('border-bottom-left-radius','5px'); //change the corner rounding to the bottom instead of the top 
  };

  // Find the appropriate thank-you and display it on the page at the appropriate spot
  // var matches = location.href.search(/(m|p)=thank\-you/);
  if (location.href.indexOf('thank-you=m') != -1) {
    if ($('#control .error').length || $('#userreg-thanks').length) {
      // do nothing for now
    }
    else {
      var thanksmsg = '<div id="userreg-thanks">' + Drupal.settings.wh_userreg.wh_userreg_thanks_message + '</div>';
      $('#control').prepend(thanksmsg);
      $('#control .status').hide();
      $('.congreet_status').hide();
    }
  }

  // Add a thank you notifier to the user registration form action
  var thanks_type = (location.search.indexOf('petitions') > -1) ? 'p' : 'm';
  $('form#user-register').each(function(index) {
    if (location.search.indexOf('thank-you=') == -1) {
      var action = $(this).attr('action');
      action = action.replace("%23", "");
      action = action + '%23thank-you=' + thanks_type;
      $(this).attr('action', action);
    }
  });

  // Check if captcha is available and enable
  if ($('div#recaptcha-ajax', context).length && $('div#recaptcha-ajax', context).not('.recaptcha-processed')) {
  	// Enable on all paths except /user/register, it has a captcha already
  	if (location.pathname.indexOf('/user/register') == -1) {
			Recaptcha.create(Drupal.settings.utility_bar.recaptcha.public_key, 'recaptcha-ajax', {
    		theme: Drupal.settings.utility_bar.recaptcha.theme,
      	callback: Recaptcha.focus_response_field
     	});
  	}
  	$('div#recaptcha-ajax', context).addClass('recaptcha-processed');
  }
}

Drupal.behaviors.utility_bar_display = function (context) {
  $("#utility-bar-wrapper").show();
}
